package fr.ippon.rh.service;

import org.jasypt.util.text.BasicTextEncryptor;
import org.springframework.stereotype.Service;

/**
 * Etape 4 du jeu.
 */
@Service
public class Etape4Service {

    private static final String etape4Texte = "";

    private static final String etape4MotDePasse = "Ceci n'est toujours pas le bon mot de passe :-((";

    /**
     * Décrypte le texte de l'étape 4.
     */
    public String decriptText() {
        BasicTextEncryptor textEncryptor = new BasicTextEncryptor();
        textEncryptor.setPassword(etape4MotDePasse);
        return textEncryptor.decrypt(etape4Texte);
    }
}
